class DataProcessor:
    def __init__(self, config):
        self.config = config
        self.data = []

    def process(self, input_data):
        # Process the input data
        for item in input_data:
            if self.validate(item):
                self.data.append(item)
        return self.data

    def validate(self, item):
        return item is not None
